SET DATABASE SQL SYNTAX MYS TRUE;
drop table t_user if exists;
create table t_user (id bigint generated by default as identity (start with 1), account varchar(255), user_name varchar(255), password varchar(255), nick_name varchar(255), user_level varchar(255), mobile varchar(255), email varchar(255), memo varchar(255), valid boolean not null, user_type tinyint(4), primary key (id));

